package practice1;

public class TypeCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 
		// Implicite Type Casting
		
     char charVar = 'A';
     int var1 = charVar ;
     System.out.print(var1);
     
     // Explicitly Type Casting
     
      char ch = 'A';
      short vr = (short)ch;
      System.out.print(vr);
     
     
	}

}
